import React, { useState } from "react";
import {
  Alert,
  Modal,
  StyleSheet,
  Text,
  TouchableHighlight,
  View,
  TextInput
} from "react-native";
import hygoStyles from '../../styles';
import COLORS from  '../../colors';
import HygoButton from '../../components/v2/HygoButton';
import { Picker } from 'native-base';


const HygoPickerModal = ({onClose, modalVisible, setModalVisible, defaultValue, items, setInput, title}) => {
    const [value, setValue] = useState<any>(defaultValue)
    return (
        <Modal
            animationType='fade'
            transparent={true}
            visible={modalVisible}
            onRequestClose={onClose}
        >
            <View style={[styles.backdrop, StyleSheet.absoluteFill]}/>
            <View style={{justifyContent: "center", flex:1}}>
              <View style={styles.modalView}>
                  <View style={styles.title}>
                    <Text style={hygoStyles.h0}>{title}</Text>
                  </View>
                  <View style={styles.input}>
                    <View style={{flex:1}}/>
                    <View style={styles.inputBorder}>
                    <Picker
                      mode='dropdown'
                      selectedValue={value}
                      onValueChange={(v, i)=>{
                        setValue(v)
                      }}
                    >
                          { items.map((v, i) => <Picker.Item label={v} value={v} key={i}/>)}     
                    </Picker>

                      {/* <TextInput 
                        onChangeText={text => setValue(text)}
                        value={value}
                        style={{ textAlign:'left', flex:1}}
                      />
                      <Text style={{paddingLeft: 10, textAlign: 'right', textAlignVertical:'center', flex:1}}>L/ha</Text> */}
                    </View>
                    <View style={{flex:1}}/>
                  </View>
                    <HygoButton 
                      label='OK'
                      onPress={() => {
                        setInput(value)
                        setModalVisible(!modalVisible);
                      }}
                      enabled={!!value}
                    />
              </View>
            </View>
      </Modal>
    )
}

const styles = StyleSheet.create({
    backdrop: {
        flex: 1,
        justifyContent: "center",
        //alignItems: "center",
        // marginTop: 22,
        backgroundColor: "black",
        opacity: 0.5
      },
      modalView: {
        //margin: 20,
        backgroundColor: "white",
        borderTopRightRadius: 20,
        // padding: 35,
        //alignItems: "center",
        shadowOffset: { width: 0, height: 2},
        shadowColor: '#000000',
        shadowRadius: 2,
        shadowOpacity: .2,
        elevation: 5,
        },
      title: {
        borderColor: '#D1CFCF', 
        borderBottomWidth: 1, 
        alignItems:'center'
      },
      input: {
        display : 'flex',
        flexDirection: 'row',
        justifyContent:'center',
        margin: 40
      },
      inputBorder: {
        display : 'flex',
        flexDirection: 'row',
        justifyContent:'center',
        borderColor: '#AAA',
        borderWidth: 1,
        flex:2,
        padding: 10,
      },
      button: {
        height: 60,
        // width: 100,
        backgroundColor: COLORS.DARK_BLUE,
        alignItems: 'center',
        justifyContent: 'center',
        borderTopRightRadius: 20,
        borderTopLeftRadius: 20,
      },
      buttonText: {
        color: '#fff',
        fontSize: 16,
        fontFamily: 'nunito-bold',
      }
  });

export default HygoPickerModal
